import { BrowserRouter } from "react-router-dom";
import Main from "./components/Main";
import { useState, React } from "react";
import logo from "./assets/items/pokedex.png";
import { Howl } from "howler";
import mainTheme from "./assets/sounds/mainTheme.mp3";

function App() {
  const [start, setStart] = useState(false);

  // var music = {
  //   overworld: new Howl({
  //     src: mainTheme,
  //     loop: true,
  //   }),
  // };

  const startPokedex = () => {
    setStart(true);
    // if (!music.overworld.playing()) {
    //   music.overworld.play();
    // }
  };

  return (
    <BrowserRouter className="App">
      {start ? (
        <Main />
      ) : (
        <div className="start-menu">
          <div className="red-background position-start-item">
            <img src={logo} />
          </div>
          <div className="ball-middle"></div>
          <div className="white-background position-start-item">
            <div className="center" onClick={startPokedex}>
              <button className="btn">
                <svg
                  width="180px"
                  height="40px"
                  viewBox="0 0 180 50"
                  className="border"
                >
                  <polyline
                    points="179,1 179,59 1,59 1,1 179,1"
                    className="bg-line"
                  />
                  <polyline
                    points="179,1 179,59 1,59 1,1 179,1"
                    className="hl-line"
                  />
                </svg>
                <span>START</span>
              </button>
            </div>
          </div>
        </div>
      )}
    </BrowserRouter>
  );
}

export default App;
